import * as l from "./layers";
import {toLonLat} from "ol/proj";
import GeoJson from "ol/format/GeoJSON";
import {sidebar} from "./map";

const navDirectionsElement = document.getElementById('nav-dir');

export function beginNav(c, m) {
    let markerSource = l.markersLayer.getSource();
    let markers = l.markersLayer.getSource().getFeatures();
    if (markers < 2 || markers > 2) {
        markerSource.clear();
        return;
    }

    let markerCoords = [];
    for (let i = 0; i < markers.length; i++) {
        markerCoords.push(toLonLat(markers[i].getGeometry().getCoordinates()));
    }
    let body = {
        coordinates: [markerCoords[0],markerCoords[1]],
        visible: false,
        layer: ''
    }

    // Find which hurricane layer is visible. Starts from largest to smallest.
    for (let j = (l.year.length - 1); j >= 0; j--) {
        for (let i = (l.day.length - 1); i >= 0; i--) {
            for (let k = 4; k >= 0; k--) {
                if ( l.hurricaneLayers['year_' + l.year[j]]['day_' + l.day[i]][k].getVisible() ) {
                    body.visible = true;
                    body.layer = (k + 1) + 'H' + l.year[j] + l.day[i];
                }
            }
        }
    }

    navDirectionsElement.innerHTML = '<strong><em>Loading Directions...</em></strong>';

    sidebar.open('nav');

    fetch('/api/ors', {
       method: 'POST',
       headers: {
           'Content-Type': 'application/json'
        },
       body: JSON.stringify(body)
    }).then(res => res.json())
        .then((geojson) => {
        console.debug(geojson);
        let features = new GeoJson().readFeatures(geojson, { featureProjection: 'EPSG:3857' });
        let coreNav = geojson.features[0].properties.segments[0];

        console.log(coreNav);

        let directions = '<ol>';

        for (let step of coreNav.steps) {
            directions += `<li>${step.instruction}</li>`;
        }
        directions += '</br><strong>Distance: ' + (geojson.features[0].properties.summary.distance / 1609.344).toFixed(2) + ' mi </br>';
        let time = geojson.features[0].properties.summary.duration;
        let hours = ((time / 3600) > 1) ? (time / 3600).toFixed(0) + " hr " : "";
        let minutes = ((time % 3600) / 60).toFixed(0) + " min ";
        let seconds = (time % 60).toFixed(0) + " sec";
        directions += 'Duration: ' + hours + minutes + seconds + '</strong></br>';
        directions += '</ol>';

        navDirectionsElement.innerHTML = directions;
        sidebar.open('nav');

        l.routingLayer.getSource().clear();
        l.routingLayer.getSource().addFeatures(features);
    });
}
